<?phpclass SD_Ordercomments_Model_Observer{        /**     * Add a customer order comment when the order is placed     * @param object $event     * @return      */    public function setOrderComment(Varien_Event_Observer $observer)    {        $orderComment = strip_tags(Mage::app()->getRequest()->getPost('sdOrderComment'));        $_order = $observer->getEvent()->getOrder();        $_order->setCustomerNote($orderComment);                       return $this;            }}