<?phpclass ServU_SourceCodes_Model_Observer{        /**     * Add a customer source code when the order is placed     * @param object $event     * @return      */    public function setSourceCode(Varien_Event_Observer $observer)    {        $sourceCode = strip_tags(Mage::app()->getRequest()->getPost('servuOrderSourceCodes'));                //Prevent order submission if source code field is empty        if(empty($sourceCode)){            $action = $observer->getEvent()->getControllerAction();            $action->setFlag('', Mage_Core_Controller_Varien_Action::FLAG_NO_DISPATCH, true);        }                //If not empty save to database        $_order = $observer->getEvent()->getOrder();        $_order->setSourcecode($sourceCode);                            return $this;    }}